  const [error, setError] = useState('');
  const [creatingShipment, setCreatingShipment] = useState(null);
  const [showCreateModal, setShowCreateModal] = useState(null);
  const [shipmentForm, setShipmentForm] = useState({
    fromLocation: '',
    toLocation: ''
  });

  useEffect(() => {
    loadOrders();
