      // For now, we'll just show a success message
      // await api.sendReportToAdmin(reportForm);
      alert('G·ª≠i b√°o c√°o th√†nh c√¥ng! (T√≠nh nƒÉng n√†y c·∫ßn API endpoint t·ª´ backend)');
      setReportForm({ title: '', content: '' });
