import React, { useState, useEffect } from 'react';
import api from '../services/api';

const DriversPage = () => {
  const [drivers, setDrivers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editingDriver, setEditingDriver] = useState(null);
  const [formData, setFormData] = useState({
    name: '',
    phone: '',
    license: '',
    userId: null
  });

  useEffect(() => {
    loadDrivers();
  }, []);

  const loadDrivers = async () => {
    try {
      setLoading(true);
      const data = await api.getAllDrivers();
      setDrivers(data);
    } catch (err) {
      setError('Kh√¥ng th·ªÉ t·∫£i danh s√°ch t√†i x·∫ø');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  const handleOpenModal = (driver = null) => {
    if (driver) {
      setEditingDriver(driver);
      setFormData({
        name: driver.name || '',
        phone: driver.phone || '',
        license: driver.license || '',
        userId: driver.userId || null
      });
    } else {
      setEditingDriver(null);
      setFormData({
        name: '',
        phone: '',
        license: '',
        userId: null
      });
    }
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
    setEditingDriver(null);
    setFormData({
      name: '',
      phone: '',
      license: '',
      userId: null
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingDriver) {
        await api.updateDriver(editingDriver.id, formData);
        alert('C·∫≠p nh·∫≠t t√†i x·∫ø th√†nh c√¥ng!');
      } else {
        await api.createDriver(formData);
        alert('Th√™m t√†i x·∫ø th√†nh c√¥ng!');
      }
      handleCloseModal();
      loadDrivers();
    } catch (err) {
      alert('L·ªói: ' + (err.message || 'Vui l√≤ng th·ª≠ l·∫°i'));
      console.error(err);
    }
  };

  const handleDelete = async (driverId) => {
    if (!window.confirm('B·∫°n c√≥ ch·∫Øc ch·∫Øn mu·ªën x√≥a t√†i x·∫ø n√†y?')) {
      return;
    }

    try {
      await api.deleteDriver(driverId);
      alert('X√≥a t√†i x·∫ø th√†nh c√¥ng!');
      loadDrivers();
    } catch (err) {
      alert('L·ªói khi x√≥a t√†i x·∫ø: ' + (err.message || 'Vui l√≤ng th·ª≠ l·∫°i'));
      console.error(err);
    }
  };

  if (loading) {
    return (
      <div className="text-center p-5">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>üë§ Qu·∫£n l√Ω T√†i x·∫ø</h2>
        <button className="btn btn-success" onClick={() => handleOpenModal()}>
          + Th√™m t√†i x·∫ø
        </button>
      </div>

      {error && (
        <div className="alert alert-warning" role="alert">
          {error}
        </div>
      )}

      <div className="card shadow-sm">
        <div className="card-body">
          <table className="table table-striped">
            <thead>
              <tr>
                <th>ID</th>
                <th>H·ªç t√™n</th>
                <th>S·ªë ƒëi·ªán tho·∫°i</th>
                <th>B·∫±ng l√°i</th>
                <th>H√†nh ƒë·ªông</th>
              </tr>
            </thead>
            <tbody>
              {drivers.length > 0 ? (
                drivers.map((driver) => (
                  <tr key={driver.id}>
                    <td>{driver.id}</td>
                    <td>{driver.name}</td>
                    <td>{driver.phone}</td>
                    <td>{driver.license}</td>
                    <td>
                      <button
                        className="btn btn-sm btn-outline-primary me-2"
                        onClick={() => handleOpenModal(driver)}
                      >
                        S·ª≠a
                      </button>
                      <button
                        className="btn btn-sm btn-outline-danger"
                        onClick={() => handleDelete(driver.id)}
                      >
                        X√≥a
                      </button>
                    </td>
                  </tr>
                ))
              ) : (
                <tr>
                  <td colSpan="5" className="text-center">
                    Ch∆∞a c√≥ t√†i x·∫ø n√†o trong h·ªá th·ªëng.
                  </td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Create/Edit Modal */}
      {showModal && (
        <div 
          className="modal show d-block" 
          tabIndex="-1" 
          style={{ 
            backgroundColor: 'rgba(0,0,0,0.5)', 
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            zIndex: 1050,
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}
          onClick={(e) => {
            if (e.target === e.currentTarget) {
              handleCloseModal();
            }
          }}
        >
          <div className="modal-dialog" onClick={(e) => e.stopPropagation()}>
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  {editingDriver ? 'S·ª≠a th√¥ng tin t√†i x·∫ø' : 'Th√™m t√†i x·∫ø m·ªõi'}
                </h5>
                <button type="button" className="btn-close" onClick={handleCloseModal}></button>
              </div>
              <form onSubmit={handleSubmit}>
                <div className="modal-body">
                  <div className="mb-3">
                    <label className="form-label">H·ªç t√™n</label>
                    <input
                      type="text"
                      className="form-control"
                      value={formData.name}
                      onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">S·ªë ƒëi·ªán tho·∫°i</label>
                    <input
                      type="text"
                      className="form-control"
                      value={formData.phone}
                      onChange={(e) => setFormData({ ...formData, phone: e.target.value })}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">B·∫±ng l√°i</label>
                    <input
                      type="text"
                      className="form-control"
                      value={formData.license}
                      onChange={(e) => setFormData({ ...formData, license: e.target.value })}
                      required
                    />
                  </div>
                  <div className="mb-3">
                    <label className="form-label">User ID (t√πy ch·ªçn)</label>
                    <input
                      type="number"
